import React, { useEffect, useRef, useState } from 'react';

const useHover = () => {
   const nodeRef = useRef()
   const [hover, setHover] = useState(false)


   const handleHover = () =>{
      nodeRef.current.ClassList.add("text-red-400")
   }
   useEffect(()=>{
      if(nodeRef.current){
         nodeRef.current.addEventListener("mouseover", handleHover);
         nodeRef.current.addEventListener("mouseout", handleOut);
      }
   },[])
   return () => {

   }
};

export default useHover;
