import React from "react";
import ReactDOM from "react-dom";
const Modal = () => {
  if (typeof document === "undefined") return <div className="modal"></div>;
  return ReactDOM.createPortal(
    <div className="fixed inset-0 z-50 flex items-center justify-center p-5 modal">
      <div className="absolute inset-0 bg-black bg-opacity-25 overlay"></div>
      <div className="modal-content z-10 relative bg-white p-10 rounded-lg max-w-[482px] w-full">
        <span className="absolute top-0 right-0 cursor-pointer">Close</span>
        <h2 className="mb-5 text-4xl font-medium font-semibold text-center">
          Welcome Back!
        </h2>
        <div className="flex flex-col gap-3">
          <label htmlFor="email" className="text-sm cursor-pointer">
            Email address
          </label>

          <input
            type="text"
            className="w-full text-sm leading-normal"
            placeholder="Enter your email"
          />
        </div>
      </div>
    </div>,
    document.querySelector("body")
  );
};

export default Modal;
