import { values } from "lodash";
import React from "react";
import { useForm } from "react-hook-form";

const SignUpFormHook = () => {
  const { register, handleSubmit } = useForm(); // useform hỗ trợ các phương thức, để nó dưới dạng destructuring để sử dụng
  const onSubmit = (values) => {
    console.log(values);
  };
  return (
    <form
      className="p-10 w-full max-w-[500px] mx-auto"
      onSubmit={handleSubmit(onSubmit)}
    >
      <div className="flex flex-col gap-2">
        <label htmlFor="firstName" className="">
          First Name
        </label>
        <input
          type="text"
          className="border rounded-md p-4 border-gray-300"
          placeholder="Enter your first name"
          id="firstName"
          name="firstName"
          {...register("fistName", {required: true,

          })}
        />
      </div>

      {/* last name */}

      <div className="flex flex-col gap-2">
        <label htmlFor="firstName" className="mt-3">
          Last Name
        </label>
        <input
          type="text"
          className="border rounded-md p-4 border-gray-300"
          placeholder="Enter your last name"
          id="lastName"
          name="lastName"
          {...register("lastName")}

        />
      </div>

      {/* email */}
      <div className="flex flex-col gap-2 mb-5">
        <label htmlFor="email" className="mt-3">
          Email
        </label>
        <input
          name="email"
          type="email"
          className="border rounded-md p-4 border-gray-300"
          placeholder="Enter your email"
          {...register("email")}

        />
        <div className="text-md text-red-500">
          {/* <ErrorMessage name="email"></ErrorMessage> */}
        </div>
      </div>

      <div className="mt-3">
        <button
          type="submit"
          className="p-4 w-full bg-blue-600 text-white font-semibold rounded-lg"
        >
          Submit
        </button>
      </div>
    </form>
  );
};

export default SignUpFormHook;
