import React from "react";
import { useFormik } from "formik";
import { values } from "lodash";

const validate = (values) => {
  const errors = {};
  if (!values.firstName) {
    errors.firstName = "Required";
  } else if (values.firstName.lenght > 20) {
    errors.firstName = "Must be 20 characters or less";
  }
  return errors;
};
const SignUpForm = () => {
  const formik = useFormik({
    initialValues: {
      firstName: "",
    },
    validate,
    onSubmit: (values) => {
      console.log(values);
    },
  });

  console.log(formik);

  return (
    <form
      onSubmit={formik.handleSubmit}
      className="p-10 w-full max-w-[500px] mx-auto"
    >
      <div className="flex flex-col gap-2">
        <label htmlFor="firstName">FirstName</label>
        <input
          type="text"
          className="border rounded-md p-4 border-gray-300"
          id="firstName"
          name="firstName"
          placeholder="Enter your first name"
          value={formik.values.firstName}
          onChange={formik.handleChange}
        />
      </div>
      {formik.errors.firstName ? (
        <div className="text-sm text-red-600">{formik.errors.firstName}</div>
      ) : null}
      <div className="mt-3">
        <button className="p-4 w-full bg-blue-600 text-white font-semibold rounded-lg">
          Submit
        </button>
      </div>
    </form>
  );
};

export default SignUpForm;
