import React from "react";
import styled from "styled-components";

// style component css
const StyledCard = styled.div`
  position: relative;
  width: 400px;
`;

const CardImageWrapper = styled.div`
  height: 400px;
  width: 100%;
  border-radius: 8px;
`;

const CardImg = styled.img`
  display: block;
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: inherit;
`;

const CardContent = styled.div`
  position: absolute;
  left: 50%;
  transform: translate(-50%, 50%);
  bottom: 0;
  border-radius: 20px;
  background-color: white;
  padding: 20px;
  z-index: 10;
  width: 100%;
`;

const CardTop = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
`;

const CardUser = styled.div`
  display: flex;
  align-items: center;
  column-gap: 12px;
`;

const UserAvatar = styled.img`
  width: 30px;
  height: 30px;
  border-radius: 100rem;
  object-fit: cover;
  flex-shrink: 0;
`;

const CardFooter = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const CartTitle = styled.h3`
  font-size: 18px;
  font-weight: 500;
  color: black;
`;

const CardAmount = styled.span`
font-size: 18px;font-weight: bold;
background: linear-gradient(86.88deg, #7D6AFF 1.38%, #FFB86C 64.35%, #FC2872 119.91%);
color: transparent;
--webkit-background-clip: text;
bac
`
const Card = () => {
  return (
    <StyledCard>
      <CardImageWrapper className="">
        <CardImg
          src="https://cdn.dribbble.com/users/2400293/screenshots/16527147/media/f079dc5596a5fb770016c4ea506cd77b.png?compress=1&resize=1200x900&vertical=top"
          alt=""
        />
      </CardImageWrapper>
      <CardContent className="">
        <CardTop className="">
          <CardUser className="">
            <UserAvatar
              src="https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80"
              alt=""
            />
            <span>@zndrson</span>
          </CardUser>
          <div className="">256</div>
        </CardTop>
        <CardFooter className="">
          <CartTitle>Cosmic Perspective</CartTitle>
          <span>12,000 PSL</span>
        </CardFooter>
      </CardContent>
    </StyledCard>
  );
};

export default Card;
