import React, { useEffect } from "react";
import { createPortal } from "react-dom";

function CreatePortal() {
  const element = document.createElement("div");
  element.id = "portal-wrapper";
  return element;
}

const portalWrapperElement = CreatePortal();

const PortalAdvance = ({
  containerClassName = "",
  bodyClassName = "",
  onClose = () => {},
  visible = false
}) => {
  useEffect(() => {
    document.body.appendChild(portalWrapperElement);
  }, []);

  const renderContent = (
    <div
      className={`fixed inset-0 ${visible? "": "opacity-0 visible"} {containerClassName}`}
      style={{
        zIndex: "9999",
      }}
    >
      <div
        className="overlay absolute inset-0 bg-black bg-opacity-20"
        onClick={onClose}
      ></div>

      <div className={`content relative z-10 ${bodyClassName}`}>
        Lorem, ipsum dolor sit amet consectetur adipisicing elit. Dolorum,
        error!
      </div>
    </div>
  );
  return createPortal(renderContent, portalWrapperElement);
};

export default PortalAdvance;
