import React, { useEffect, useState } from "react";
import axios from "axios";

const getPhotos = () => {
  //thằng getphotos khi sử dụng axios thì nó đã trở thành promis nên có thể xài then
  return axios
    .get("https://picsum.photos/v2/list?limit=8")
    .then((response) => {
      console.log(response);
      return response.data; //data là props của cái api picsum
    })
    .catch((error) => {
      console.log(error);
    });
};
//https://picsum.photos/v2/list?page=2&limit=100
const Photos = () => {
  const [randomPhotos, setRandomPhotos] = useState([]);
  useEffect(() => {
    getPhotos().then((images) => {
      setRandomPhotos(images);
    });
  }, []);
  return (
    <div className="grid grid-cols-4 gap-5 p-5">
      {randomPhotos.length > 0 &&
        randomPhotos.map((item, index) => (
          <div
            key={item.id}
            className="p-3 bg-white shadow-md rounded-lg h-[200px]"
          >
            <img
              src={item.download_url}
              alt={item.author}
              className="w-full h-full rounded-lg object-cover"
            />
          </div>
        ))}

        <button>Load more</button>
    </div>
  );
};

export default Photos;
