import React from "react";
import { useFormik, Formik, Form, ErrorMessage, Field } from "formik";
import { values } from "lodash";
import * as Yup from "yup";

const SignUpFormFinal = () => {
  const formik = useFormik({
    initialValues: {
      firstName: "",
      lastName: "",
    },
    validationSchema: Yup.object({
      firstName: Yup.string()
        .max(20, "Must be 20 characters or less")
        .required("Required"),
      lastName: Yup.string()
        .max(20, "Must be 20 characters or less")
        .required("Required"),
    }),

    onSubmit: (values) => {
      console.log(values);
    },
  });

  console.log(formik);

  return (
    <Formik
      initialValues={{
        firstName: "",
        lastName: "",
      }}
      validationSchema={Yup.object({
        firstName: Yup.string()
          .max(20, "Must be 20 characters or less")
          .required("Required"),
        lastName: Yup.string()
          .max(20, "Must be 20 characters or less")
          .required("Required"),
      })}
      onSubmit={(values) => {
        console.log(values);
      }}
      className="p-10 w-full max-w-[500px] mx-auto"
    >
      <div className="flex flex-col gap-2">
        <label htmlFor="firstName">First Name</label>
        <input
          type="text"
          className="border rounded-md p-4 border-gray-300"
          placeholder="Enter your first name"

        />
      </div>


      {/* last name */}

      <div className="flex flex-col gap-2">
        <label htmlFor="firstName">Last Name</label>
        <input
          type="text"
          className="border rounded-md p-4 border-gray-300"
          placeholder="Enter your last name"
          {...formik.getFieldProps("lastName")}
        />
      </div>
      {formik.touched.lastName && formik.errors.lastName ? (
        <div className="text-sm text-red-600">{formik.errors.lastName}</div>
      ) : null}

      <div className="mt-3">
        <button
          type="submit"
          className="p-4 w-full bg-blue-600 text-white font-semibold rounded-lg"
        >
          Submit
        </button>
      </div>
    </Formik>
  );
};

export default SignUpFormFinal;
