import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import lodash from "lodash";
//https://hn.algolia.com/api/v1/search?query=react
const HackerNews = () => {
  const [hits, setHits] = useState([]);
  const [query, setQuery] = useState("");
  const handleFetchData = useRef({}); //useRef luu cac gia tri ben ngoai compo, o truong hop nay la query
  const [loading, setLoading] = useState(true);

  handleFetchData.current = async () => {
    setLoading(true);
    try {
      const response = await axios.get(
        `https://hn.algolia.com/api/v1/search?query=${query}`
      );
      console.table(response.data.hits);
      setHits(response.data?.hits || []);
      setLoading(false);
    } catch {
      setLoading(true);
      throw new Error("something happened with api");
    }
  };

  useEffect(() => {
    handleFetchData.current();
  }, [query]);

  const handleUpdateQuery = lodash.debounce((e) => {
    setQuery(e.target.value);
  }, 500);

  return (
    <div className="bg-white mx-auto mt-5 p-5 rounded-lg shadow-md w-2/4">
      <input
        type="text"
        defaultValue={query}
        className="border border-gray-200 p-5 block w-full rounded-md transition-all text-black focus:border-blue"
        placeholder="Typing your keyword..."
        onChange={handleUpdateQuery}
      />
      {loading && (
        <div className="loading w-8 h-8 rounded-full border-r-4 border-4 border-blue-600 border-r-transparent animate-spin mx-auto "></div>
      )}
      {!loading &&
        hits.length > 0 &&
        hits.map((item, index) => <h3 key={index}> {item.title}</h3>)}
    </div>
  );
};

export default HackerNews;
