import React, { useEffect, useRef, useState } from "react";

const useHover = () => {
  const nodeRef = useRef();
  const [hover, setHover] = useState(false);

  const handleHover = () => {
    nodeRef.current.ClassList.add("text-red-400");
  };
  const handleOut = () => {};
  const dom = nodeRef.current;
  useEffect(() => {
    if (dom) {
      dom.addEventListener("mouseover", handleHover);
      dom.addEventListener("mouseout", handleOut);
    }
    return () => {
      if (dom) {
        dom.removeEventListener("mouseover", handleHover);
        dom.removeEventListener("mouseout", handleOut);
      }
    };
  }, []);
};

export default useHover;
