import React, { useEffect, useState } from "react";
import axios from "axios";

const getPhotos = () => {
  //thằng getphotos khi sử dụng axios thì nó đã trở thành promis nên có thể xài then
  return axios
    .get("https://picsum.photos/v2/list")
    .then((response) => {
      console.log(response);
      return response.data; //data là props của cái api picsum
    })
    .catch((error) => {
      console.log(error);
    });
};

const Photos = () => {
  const [randomPhotos, setRandomPhotos] = useState([]);
  useEffect(() => {
    getPhotos().then((images) => {
      setRandomPhotos(images);
    });
  }, []);
  return (
    <div className="grid grid-cols-4 gap-5 p-5">
      {randomPhotos.map((item, index) => (
        <div key={item.id} className="p-3 bg-white shadow-md rounded-lg">
          <img src={item.download_url} alt="" className="w" />
          {item.author}
        </div>
      ))}
    </div>
  );
};

export default Photos;
