import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
//https://hn.algolia.com/api/v1/search?query=react
const HackerNews = () => {
  const [hits, setHits] = useState([]);
  const [query, setQuery] = useState("react");
  const handleFetchData = useRef({}); //useRef luu cac gia tri ben ngoai compo, o truong hop nay la query
  const [loading, setLoading] = useState(true);
  handleFetchData.current = async () => {
    const response = await axios.get(
      `https://hn.algolia.com/api/v1/search?query=${query}`
    );
    console.table(response.data.hits);
    setHits(response.data?.hits || []);
  };

  useEffect(() => {
    handleFetchData.current();
  }, [query]);

  return (
    <div>
      <input
        type="text"
        className="border border-green-500 text-black p-5"
        onChange={(e) => {
          setQuery(e.target.value);
        }}
      />
      {loading }
      {hits.length > 0 &&
        hits.map((item, index) => <h3 key={index}> {item.title}</h3>)}
    </div>
  );
};

export default HackerNews;
