import React, { useEffect, useState } from "react";
import axios from "axios";

const getPhotos = (page) => {
  //thằng getphotos khi sử dụng axios thì nó đã trở thành promis nên có thể xài then

  return axios
    .get(`https://picsum.photos/v2/list?page=${page}&limit=8`)
    .then((response) => {
      console.log(response);
      return response.data; //data là props của cái api picsum
    })
    .catch((error) => {
      console.log(error);
    });
};
const Photos = () => {
  const handleLoadMore = () => {
    getPhotos(nextPage).then((images) => {
      setRandomPhotos(images);

      const newPhotos = [
        //ở đây sử dụng spread operator để nối giá trị cũ và giá trị mới
        ...randomPhotos,
        images,
      ];
      setNextPage(nextPage + 1);
      console.log("present page: ", nextPage);
    });
  };

  const [randomPhotos, setRandomPhotos] = useState([]);
  const [nextPage, setNextPage] = useState(1);

  useEffect(() => {
    handleLoadMore();
  }, []);

  return (
    <div>
      <div className="grid grid-cols-4 gap-5 p-5">
        {randomPhotos.length > 0 &&
          randomPhotos.map((item, index) => (
            <div
              key={item.id}
              className="p-3 bg-white shadow-md rounded-lg h-[200px]"
            >
              <img
                src={item.download_url}
                alt={item.author}
                className="w-full h-full rounded-lg object-cover"
              />
            </div>
          ))}
      </div>
      <div className="text-center">
        <button
          className="inline-block  px-8 py-4 bg-purple-600 text-white justify-center"
          onClick={handleLoadMore}
        >
          Load more
        </button>
      </div>
    </div>
  );
};

export default Photos;
